mapper.select:
SELECT idimage FROM
`image`
WHERE date >=
#{from_}
AND date
<=#{to_}

SELECT idimage FROM
`image`
WHERE target LIKE #{object_}

mapper.update:
UPDATE `image`
SET
jpg=#{jpg_}
WHERE
rt=#{rt_} AND
local_id=#{lid_}

UPDATE `image`
SET
fits=#{fits_}
WHERE
rt=#{rt_} AND
local_id=#{lid_}

UPDATE `image`
SET
target=#{target_}
WHERE
rt=#{rt_} AND
local_id=#{lid_}
